package basicprograms;

public class LoopingEg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		for(int i=1;i<5;i++) {
			System.out.println(i);
		}
		int j=1;
		while(j<5) {
			System.out.println(j);
			j++;		
		}
		int m=1;
		do {
			System.out.println(m);
			m++;	
		}while(m<5);
	}

}
